declare module '*.png' {
  const image: string
  export default image
}

declare module '*.jpg' {
  const image: string
  export default image
}

declare module '*.jpeg' {
  const image: string
  export default image
}

declare module '~icons/*' {
  const icon: string
  export default icon
}

declare module '*.mp3' {
  const Sound: string
  export default Sound
}

declare module '*.gif' {
  const image: string
  export default image
}
